INSERT INTO `category_translations` (`id`, `title`, `short_description`, `description`, `seo_key`, `seo_des`, `category_id`, `locale`, `created_at`, `updated_at`) VALUES
(1, 'نماذج اولية سريعة', 'يمكن استيراد تصميمات متعددة لأجزاء مختلفة في نفس الوقت لإنتاج عرض أسعار واحد. في أقل من دقيقة ، يظهر تقدير تفصيلي للتكلفة على الشاشة', '<div>هنالك العديد من الأنواع المتوفرة لنصوص لوريم إيبسوم، ولكن الغالبية تم تعديلها بشكل ما عبر إدخال بعض النوادر أو الكلمات العشوائية إلى النص. إن كنت تريد أن تستخدم نص لوريم إيبسوم ما، عليك أن تتحقق أولاً أن ليس هناك أي كلمات أو عبارات محرجة أو غير لائقة مخبأة في هذا النص. بينما تعمل جميع مولّدات نصوص لوريم إيبسوم على الإنترنت على إعادة تكرار مقاطع من نص لوريم إيبسوم نفسه عدة مرات بما تتطلبه الحاجة، يقوم مولّدنا هذا باستخدام كلمات من قاموس يحوي على أكثر من 200 كلمة لا تينية، مضاف إليها مجموعة من الجمل النموذجية، لتكوين نص لوريم إيبسوم ذو شكل منطقي قريب إلى النص الحقيقي. وبالتالي يكون النص الناتح خالي من التكرار، أو أي كلمات أو عبارات غير لائقة أو ما شابه. وهذا ما يجعله أول مولّد نص لوريم إيبسوم حقيقي على الإنترنت.</div><div><br></div>', NULL, NULL, 1, 'ar', NULL, NULL),
